import styled from "styled-components";

export const Container = styled.div`
  text-align: center;
  align-items: center;
  margin: 0 auto;
`

export const ContainerDados = styled.div`
  display: flex;
  justify-content: space-between;
  margin: 2% 0;
  @media (max-width: 700px) {
		margin: 2% 10%;
		width: 300px;
	}
`
