import styled from "styled-components";

export const DivCreateComments = styled.div`
  text-align: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 0 7%;
`

export const H1 = styled.h1`
  font-size: 42px;
  color: #21CDD9;
  padding-top: 20px;
  @media (max-width: 700px) {
		padding: 2% 4%;
    font-size: 45px;
    text-align: left;
	}
`
