import React, { useEffect, useState } from "react";
import axios from "axios";
import { Auth, Post_url } from "../../constants/urls";
import useForm from "../../hooks/useForm";

export default function CreateComments() {
  const [form, onChange, clear ] = useForm({name:''})

  let idComments = localStorage.getItem('idComments')

  const commentsCreate = () => {
    axios.post(`${Post_url}/${idComments}/comments`, form, Auth)
    .then((res) => {
      alert('Comentário enviado com sucesso!')
    })
    .catch((err) => {
      console.log('error', err.response);
    })

  }

  const keyPressEnter = (e) => {
    if(e.key === 'Enter' && form.body !== ''){
      commentsCreate();
    }
  }
  useEffect(() => {
    commentsCreate()
  }, [])


  return (
    <div>
      <h1>Escreva um comentário</h1>
      <form onSubmit={commentsCreate}>
        <input required name="name" type={'text'} value={form.body} onChange={onChange} placeholder="Envie seu comentário"></input>
        <button onClick={commentsCreate}>Enviar comentário</button>
      </form>
    </div>
  );
}
