import { createTheme } from '@material/system';
import { createMuiTheme } from '@mui/material';
import { backgroundColor } from './colors';

const theme = createMuiTheme({
  palette: {
    main: backgroundColor,
    contrastText: 'white'
  }
})
export default theme
