import React, { useEffect, useState } from "react";
import axios from "axios";
import { Auth, Post_url } from "../../../constants/urls";
import { DivCreateComments, H1 } from "../styledComments";
import { ContainerCard, Loading } from "../../post/styledPost";
import Gif from '../../../constants/imgs/loading.gif';

export default function ViewsComments() {
  const [ infoDetailPost, setInfoDetailPost] = useState([])

  let idComments = localStorage.getItem('idComments')

  const getCommentsId = () => {
    axios.get(`${Post_url}/${idComments}/comments`, Auth)
    .then((res) => {
      setInfoDetailPost(res.data);
      console.log(res.data);
    })
    .catch((err) => {
  })
  }

  useEffect(() => {
    getCommentsId();
  }, [getCommentsId()])

  return (
    <DivCreateComments>
      <H1>Todos os comentários</H1>
      {infoDetailPost && infoDetailPost.length > 0 ? infoDetailPost.map((posts) => {
          return <ContainerCard key={posts.id}>
            <div>
                <p>{posts.username}</p>
                <p>{posts.body}</p>
                <p>{posts.voteSum}</p>
            </div>
            </ContainerCard>
        }) :(
          <div>
            <h1>Aguarde, pois estamos Carregando os comentários...</h1>
            <Loading src={Gif} />
          </div>
        )}
    </DivCreateComments>
  );
}
