import styled from "styled-components";

export const Header = styled.header`
  height: 7vh;
  background-color: #69ceff;
  text-align: right;
  display: flex;
  justify-content: space-between;
  padding: 0 1%;
`

export const AClick = styled.a`
  margin: 0 20px;
  width: 70px;
  font-size: 22px;
  color: black;
  font-weight: bold;
  align-items: center;
  display: flex;
  cursor: pointer;
  :hover{
    color: blue;
  }
`

export const Img = styled.img`
  width: 8%;
  @media (max-width: 700px) {
		margin: 3% 0;
    width: 15%;
    text-align: center;
	}
`

export const Fundo = styled.div`
  background-color: #151F28;
  height: 100vh;
  color: white;
`
