import styled from "styled-components";

export const Container = styled.div`
  text-align: center;
`

export const ContainerDados = styled.div`
  display: flex;
  justify-content: space-between;
  text-align: center;
  align-items: center;
  margin: 2% 0;
  @media (max-width: 700px) {
    display: flex;
    flex-direction: column;
	}
`
export const Buttons = styled.button`
  width: 500px;
  height: 40px;
  color: #21CDD9;
  font-weight: bold;
  font-size: 18px;
  cursor: pointer;
  margin-top: 20px;
  background-color: transparent;
  border-radius: 20px;
  border-color: #21CDD9;
  :hover{
    background-color: #21CDD9;
    color: black;
    transition: 0.5s;
  }
  @media (max-width: 700px) {
      width: 80%;
      margin: 2%;
      margin-bottom: 10%;
	}
`
