import styled from "styled-components";

export const Form = styled.form`
	display: flex;
	flex-direction: column;
	width: 70%;
	margin: 3% auto;
	@media (max-width: 700px) {
		margin: 2% 10%;
		width: 300px;
	}
	@media (min-width: 1600px) {
		width: 80%;
		margin: 8% auto;
	}
`
export const Input = styled.input`
	background: rgba(33,205,217,1);
	height: 45px;
	margin: 1% 0;
	padding: 10px 30px;
  border-radius: 220px;
	font-weight: bolder;
  outline: none;
  border-color: #21CDD9;

	::placeholder {
       color: white;
   }
	 @media (min-width: 1600px) {
		height: 60px;
	}
`
