import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Auth, IMG_url, Post_url } from '../../../constants/urls';
import {
	Body,
	ContainerCard,
	ContainerInfo,
	ContainerProfile,
	ImgCards,
	ImgCardsComent,
	ImgPost,
	ImgProfile,
	Loading,
	ParagrafoText
} from '../styledPost';
import Gif from '../../../constants/imgs/loading.gif';

import darLike from '../../../constants/imgs/darLike.png';
import Like from '../../../constants/imgs/like.png';

import desLike from '../../../constants/imgs/deslike.png';
import darDesLike from '../../../constants/imgs/darDeslike.png';

import Comentar from '../../../constants/imgs/comentarios.png';
import Compatilhar from '../../../constants/imgs/compartilhar.png';
import { toast } from 'react-toastify';

export default function ViewPost() {
	const navigate = useNavigate();
	const [ infoPost, setInfoPost ] = useState([]);
	const [ info, setInfo ] = useState([]);
	const [ clickVote, setClickVote] = useState(0)
	const [ clickVoteDeslike, setClickVoteDeslike] = useState(0)


	const getPosts = () => {
		axios
			.get(Post_url, {
				headers: {
					Authorization: localStorage.getItem('tokenEddit')
				}
			})
			.then((res) => {
				setInfoPost(res.data);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	const getPhotos = () => {
		axios
			.get(IMG_url)
			.then((res) => {
				setInfo(res.data.hits);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	const idComments = (id) => {
		localStorage.setItem('idComments', id);
		navigate('/comments-post');
	};

	const postVoteLike = (idVote) => {
		const body = {
			direction:  1,
		}
		axios.post(`${Post_url}/${idVote}/votes`, body, {
			headers: {
				Authorization: localStorage.getItem('tokenEddit')
			}
		})
		.then((res) => {
			setClickVote(1)
		})
		.catch((err) => {
			console.log(err.response)
		})
	};
	const postVoteDeslike = (idVote) => {
		const body = {
			direction:- 1,
		}
		axios.put(`${Post_url}/${idVote}/votes`, body, {
			headers: {
				Authorization: localStorage.getItem('tokenEddit')
			}
		})
		.then((res) => {
			clickVoteDeslike(-1)
			clickVote(0)
		})
		.catch((err) => {
			console.log(err.response)
		})
	};

	let IconsLike
	if(clickVote === 0){
		IconsLike = darLike
	}
	if(clickVote === 1){
		IconsLike = Like
	}
	let IconsDeslike
	if(clickVoteDeslike === -1){
		IconsDeslike = darDesLike
	}
	if(clickVoteDeslike === 0){
		IconsDeslike = desLike
	}
	useEffect(
		() => {
			getPosts();
			getPhotos();
			postVoteLike();
			postVoteDeslike();
		},
		[ getPosts()]
	);

	return (
		<div>
			{infoPost && infoPost.length > 0 ? (
				infoPost.map((posts, index) => {
					return (
						<ContainerCard key={posts.id}>
							<ContainerProfile>
								<ImgProfile src={info[index +1].userImageURL} />
								<ParagrafoText>{posts.title}</ParagrafoText>
								<p>@{posts.username}</p>
							</ContainerProfile>
							<div>
								<ImgPost src={info[index].largeImageURL} />
								<Body>{posts.body}</Body>
							</div>
							<ContainerInfo>
								<p>{posts.voteSum && posts.voteSum.length >= 1 ? <p>curtidas: {posts.voteSum}</p> : <p>curtidas: 0</p>}</p>
								<ImgCards onClick={() => postVoteLike(posts.id)} src={IconsLike} alt="dar like" />
								<ImgCards onClick={() => postVoteDeslike(posts.id)} src={IconsDeslike} alt="dar deslike" />
								<ImgCards src={Compatilhar} alt="Compartilhar" />
								<ImgCardsComent onClick={() => idComments(posts.id)} src={Comentar} alt="dar like" />
								<p>{posts.commentCount && posts.commentCount.length >= 1 ? (<p>comentários: {posts.commentCount}</p>) : <p>comentário: 0</p> }</p>
							</ContainerInfo>
						</ContainerCard>
					);
				})
			) : (
				<div>
					<h1>Aguarde, pois estamos Carregando posts...</h1>
					<Loading src={Gif} />
				</div>
			)}
		</div>
	);
}
