import React, { useEffect, useState } from "react";
import CheckLogin from "../../../assents/checkLogin";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Post_url } from "../../../constants/urls";

export default function ViewPost(){
  const navigate = useNavigate();
  const [ infoPost, setInfoPost] = useState([])

  const getPosts = () => {
    axios.get(Post_url, {
      headers: {
        Authorization: localStorage.getItem('tokenEddit')
      }
		})
    .then((res) => {
      setInfoPost(res.data);
    })
    .catch((err) => {
      console.log(err.response);
    })
  }

  const idComments = (id) => {
    localStorage.setItem('idComments', id)
  }

  useState(() => {
    getPosts()
  }, [getPosts()])

  return (
    <div>
      <div>
        {infoPost && infoPost.length > 0 ? infoPost.map((posts) => {
          return <div key={posts.id}>
            <div>
               <p>{posts.username}</p>
               <p>{posts.title}</p>
               <p>{posts.createdAt}</p>
            </div>
            <div>
              {posts.body}
            </div>
            <div>
              {posts.voteSum}
              {posts.commentCount}
            </div>
            </div>
        }) :  <p>Aguarde o carregamento dos posts...</p>}
      </div>

    </div>
  );
}
