import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Post_url } from "../../constants/urls";

export default function Comments() {
  const navigate = useNavigate();
  const [ infoDetailPost, setInfoDetailPost] = useState([])

  let idComments = localStorage.getItem('idComments')

  const getPosts = () => {
    axios.get(`${Post_url}/${idComments}/comments`, {
      headers: {
        Authorization: localStorage.getItem('tokenEddit')
      }
		})
    .then((res) => {
      setInfoDetailPost(res.data);
      console.log(res.data);
    })
    .catch((err) => {
      console.log(err.response);
    })
  }

  const returnPosts = (id) => {
    navigate('/posts') 
  }

  useEffect(() => {
    getPosts()
  }, [])

  return (
    <div>
      <h1>Sou o comments</h1>
      {infoDetailPost.map((posts) => {
          return <div key={posts.id}>
            <div>
               <p>{posts.username}</p>
              <p>{posts.body}</p>
              <p>{posts.voteSum}</p>
            </div>
            <div>
              <div onClick={returnPosts}>{posts.commentCount}</div>
            </div>
            </div>
        })}
    </div>
  );
}
