import React, { useEffect, useState } from "react";
import axios from "axios";
import { Auth, Post_url } from "../../constants/urls";
import useForm from "../../hooks/useForm";
import { useNavigate } from "react-router-dom";

export default function CreateComments() {
/*   const [form, onChange ] = useForm({body:''})
 */ const [coments, setComents] = useState('');
    const [gabiarra, setGambiarra] = useState('')

 const onChange = (e) => {
   setComents(e.target.value)
 }

  let idComments = localStorage.getItem('idComments')

  const commentsCreate = () => {    
    const body = {
      body: coments
    }
    axios.post(`${Post_url}/${idComments}/comments`, body, Auth)
    .then((res) => {
      alert('Comentário enviado com sucesso!',res)
      setGambiarra(coments)
      this.getCommentsId()
    })
    .catch((err) => {
      alert('Sentimos muito, mas não foi possível enviar esse comentário!')
    })
  }

  const keyPressEnter = (e) => {
    if(e.key === 'Enter' && coments !== ''){
      commentsCreate();
    }
  }

  return (
    <div>
      <form onSubmit={commentsCreate}>
        <input onKeyPress={keyPressEnter} required type={'text'} value={coments} onChange={onChange} placeholder="Envie seu comentário"></input>
        <button onKeyPress={keyPressEnter} onClick={commentsCreate}>Enviar comentário</button>
      </form>
    </div>
  );
}
