import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Post_url } from '../../constants/urls';
import useForm from '../../hooks/useForm';

export default function Comments() {
	const navigate = useNavigate();
	const [ infoDetailPost, setInfoDetailPost ] = useState([]);
	const [ coment, setComent ] = useState('');

	let idComments = localStorage.getItem('idComments');

  const onComent = (e) => {
    setComent(e.target.value)
  }

	const getCommentsId = () => {

    console.log(localStorage.getItem('tokenEddit'));
		axios
			.get(`${Post_url}/${idComments}/comments`, {
				headers: {
					Authorization: localStorage.getItem('tokenEddit')
				}
			})
			.then((res) => {
				setInfoDetailPost(res.data);
				console.log('passou aqui');
			})
			.catch((err) => {
				alert('Problema na requisição dos comentários');
        console.log(err.response);
			});
	};

	const createComments = () => {
    const body = {
      body: coment
    }

		axios
			.post(`${Post_url}/${idComments}/comments`, body, {
				headers: {
					Authorization: localStorage.getItem('tokenEddit')
				}
			})
			.then((res) => {
        console.log('passou aqui tambem', res);
				setComent(res)
			})
			.catch((err) => {
				console.log('error', err.response);
			});
	};

	const keyPressEnter = (e) => {
		if (e.key === 'Enter' && coment !== '') {
			createComments();
		}
	};

	const returnPosts = () => {
		localStorage.removeItem('idComments');
		navigate('/posts');
	};

	useEffect(() => {
		getCommentsId();
	}, [getCommentsId()]);

	return (
		<div>
			<h1>Escreva um comentário</h1>
			<form>
				<input
					onKeyPress={keyPressEnter}
					required
					name="body"
					value={coment}
					onChange={onComent}
					placeholder="Envie seu comentário"
				/>
				<button onKeyPress={keyPressEnter} onClick={createComments}>
					Enviar comentário
				</button>
			</form>
			<h1>Todos os comentários</h1>
			{infoDetailPost && infoDetailPost.length > 0 ? (
				infoDetailPost.map((posts) => {
					return (
						<div key={posts.id}>
							<div>
								<p>{posts.username}</p>
								<p>{posts.body}</p>
								<p>{posts.voteSum}</p>
							</div>
						</div>
					);
				})
			) : (
				<p>Carregando comentários</p>
			)}
			<button onClick={returnPosts}>Voltar aos posts</button>
		</div>
	);
}
