import React, { useEffect, useState } from "react";
import axios from "axios";
import { Auth, Post_url } from "../../constants/urls";
/* import useForm from "../../hooks/useForm";
 */
export default function CreateComments() {
/*  const [form, onChange ] = useForm({body:''})   */  

  const [coment, setComents] = useState('')
  const [gambiarra, setGambiarra] = useState('')
  let idComments = localStorage.getItem('idComments')

  const onChange = (e) => {
    setComents(e.target.value)
  }

  const commentsCreate = () => {
    const body = {
      body: coment
    }
    axios.post(`${Post_url}/${idComments}/comments`, body, Auth)
    .then((res) => {
      setGambiarra(res)
      alert('Coment치rio enviado com sucesso!')
    })
    .catch((err) => {
      console.log('error', err.response);
    })
  }

  const keyPressEnter = (e) => {
    if(e.key === 'Enter' && coment !== ''){
      commentsCreate();
    }
  }
  useEffect(() => {
    commentsCreate();
  }, [gambiarra])

  return (
    <div>
      <form onSubmit={commentsCreate}>
        <input onKeyPress={keyPressEnter} required type={'text'} value={coment} onChange={onChange} placeholder="Envie seu coment치rio"></input>
        <button onKeyPress={keyPressEnter} onClick={commentsCreate}>Enviar coment치rio</button>
      </form>
    </div>
  );
}
