import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Auth, Post_url } from "../../constants/urls";
import useForm from "../../hooks/useForm";

export default function Comments() {
  const navigate = useNavigate();
  const [form, onChange, clear ] = useForm({body:''})
  const [ infoDetailPost, setInfoDetailPost] = useState([])

  let idComments = localStorage.getItem('idComments')

  const getCommentsId = () => {
    axios.get(`${Post_url}/${idComments}/comments`, Auth)
    .then((res) => {
      setInfoDetailPost(res.data);
    })
    .catch((err) => {
      alert('Problema na requisição dos comentários')
    })
  }

  const returnPosts = () => {
    localStorage.removeItem('idComments');
    navigate('/posts') 
  }

  const commentsCreate = () => {    
    axios.post(`${Post_url}/${idComments}/comments`, form, Auth)
    .then((res) => {
      alert('Comentário enviado com sucesso!');
      clear();
    })
    .catch((err) => {
      console.log('Virei um looping');
    })
  }

  const keyPressEnter = (e) => {
    if(e.key === 'Enter' && form.body !== ''){
      commentsCreate();
    }
  }

  useEffect(() => {
    getCommentsId();
    commentsCreate();
  }, [commentsCreate()])

  return (
    <div>
      <h1>Escreva um comentário</h1>
      <form onSubmit={commentsCreate}>
        <input onKeyPress={keyPressEnter} required name="body" type={'text'} value={form.body} onChange={onChange} placeholder="Envie seu comentário"></input>
        <button onKeyPress={keyPressEnter} onClick={commentsCreate}>Enviar comentário</button>
      </form>
      <h1>Todos os comentários</h1>
      {infoDetailPost && infoDetailPost.length > 0 ? infoDetailPost.map((posts) => {
          return <div key={posts.id}>
            <div>
                <p>{posts.username}</p>
                <p>{posts.body}</p>
                <p>{posts.voteSum}</p>
            </div>
            </div>
        }) : <p>Carregando comentários</p>}
        <button onClick={returnPosts}>Voltar aos posts</button>
    </div>
  );
}
