import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { IMG_url, Post_url } from '../../../constants/urls';
import { ImgPost, ImgProfile, Loading } from '../styledPost';
import Gif from '../../../constants/imgs/loading.gif'
import { Profile } from '../../../components/Profile';

export default function ViewPost() {
	const navigate = useNavigate();
	const [ infoPost, setInfoPost ] = useState([]);
	const [ count, setCount ] = useState(0)
	const [ result, setResult] = useState(Profile)

	console.log(infoPost);

	const getPosts = () => {
		axios
			.get(Post_url, {
				headers: {
					Authorization: localStorage.getItem('tokenEddit')
				}
			})
			.then((res) => {
				setInfoPost(res.data);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	const counts = () => {
		if(count => 0 && count < 10){
			setCount(count + 1)
		}
		else {
			setCount(0)
		}
	}

	useEffect(() => {
			getPosts();
		},[getPosts()]
	);

	const idComments = (id) => {
		localStorage.setItem('idComments', id);
		navigate('/comments-post');
	};

	return (
		<div>
			<h1>Todos os Post</h1>
			<ImgProfile src={result[count].profile}></ImgProfile>
			<ImgPost src={result[count].post}></ImgPost>
			<div>
				{infoPost && infoPost.length > 0 ? (
					infoPost.map((posts) => {
						return (
							<div key={posts.id}>
								<div>
								<ImgProfile src={result[count].profile}></ImgProfile>
									<p>{posts.username}</p>
									<p>{posts.title}</p>
								</div>
								<div>
									<ImgPost src={result[count].post}></ImgPost>
									{posts.body}
									</div>
								<div>
									<p>{posts.voteSum}</p>
									<p onClick={() => idComments(posts.id)}>
										Ver todos os {posts.commentCount} comentários
									</p>
								</div>
							</div>
						);
					})
				) : ( <div>
					<h1>Carregando posts...</h1>
					<Loading src={Gif}></Loading>
					</div>
				)}
			</div>
		</div>
	);
}
