import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Auth, IMG_url, Post_url } from '../../../constants/urls';
import {
	Body,
	ContainerCard,
	ContainerInfo,
	ContainerProfile,
	ImgCards,
	ImgCardsComent,
	ImgPost,
	ImgProfile,
	Loading,
	ParagrafoText
} from '../styledPost';
import Gif from '../../../constants/imgs/loading.gif';

import darLike from '../../../constants/imgs/darLike.png';
import Like from '../../../constants/imgs/like.png';

import desLike from '../../../constants/imgs/deslike.png';
import darDesLike from '../../../constants/imgs/darDeslike.png';

import Comentar from '../../../constants/imgs/comentarios.png';
import Compatilhar from '../../../constants/imgs/compartilhar.png';
import { toast } from 'react-toastify';

export default function ViewPost() {
	const navigate = useNavigate();
	const [ infoPost, setInfoPost ] = useState([]);
	const [ info, setInfo ] = useState([]);
	const [ clickVote, setClickVote] = useState(0)

	const getPosts = () => {
		axios
			.get(Post_url, Auth)
			.then((res) => {
				setInfoPost(res.data);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	const getPhotos = () => {
		axios
			.get(IMG_url)
			.then((res) => {
				setInfo(res.data.hits);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	useEffect(
		() => {
			getPosts();
			getPhotos();
			postVote();
		},
		[ infoPost ]
	);

	const idComments = (id) => {
		localStorage.setItem('idComments', id);
		navigate('/comments-post');
	};

	const infoVoteLike = (infoClick) => {
		localStorage.setItem('infoClick', infoClick);
		setClickVote(1)
	}
	const infoVoteDeslike = (infoClick) => {
		localStorage.setItem('infoClick', infoClick);
		setClickVote(-1)
	}

	const postVote = () => {
		const idVote = localStorage.getItem('infoClick')
		const body = {
			direction: clickVote,
		}
		axios.post(`${Post_url}/${idVote}/votes`, body, Auth)
		.then((res) => {

		})
		.catch((err) => {
			toast.error('Problema com o like/deslike')
		})
	};

	return (
		<div>
			{infoPost && infoPost.length > 0 ? (
				infoPost.map((posts, index) => {
					return (
						<ContainerCard key={posts.id}>
							<ContainerProfile>
								<ImgProfile src={info[index +1].userImageURL} />
								<ParagrafoText>{posts.title}</ParagrafoText>
								<p>@{posts.username}</p>
							</ContainerProfile>
							<div>
								<ImgPost src={info[index].largeImageURL} />
								<Body>{posts.body}</Body>
							</div>
							<ContainerInfo>
								<p>curtidas: {posts.voteSum}</p>
								<ImgCards onClick={() => infoVoteLike(posts.id)} src={darLike} alt="dar like" />
								<ImgCards onClick={() => infoVoteDeslike(posts.id)} src={darDesLike} alt="dar deslike" />
								<ImgCards src={Compatilhar} alt="Compartilhar" />
								<ImgCardsComent onClick={() => idComments(posts.id)} src={Comentar} alt="dar like" />
								<p>coment√°rios: {posts.commentCount}</p>
							</ContainerInfo>
						</ContainerCard>
					);
				})
			) : (
				<div>
					<h1>Aguarde, pois estamos Carregando posts...</h1>
					<Loading src={Gif} />
				</div>
			)}
		</div>
	);
}
