import styled from "styled-components";

export const Footer = styled.footer`
  height: 4vh;
  background-color: #69ceff;
  padding: 0 1%;
  position: fixed;
  margin-top: auto;
  display: flex;
  color: black;
  text-align: center;
  align-items: center;
  padding: 2px 0;
`
