import styled from "styled-components";

export const TamanhoDivImg = styled.div`
  width: 48%;
  margin: 2%;
`

export const TamanhoImg = styled.img`
  width: 50%;
`

export const H1 = styled.h1`
  font-size: 52px;
  padding-right: 30%;
  color: #21CDD9;
`

export const FundoBalao = styled.div`
    margin: 0 auto; 
    background: #7e57c2;
    font-family: 'open sans';
    font-size: 0.85em;
    line-height: 1.6em;  
    border-radius: 15px;
    width: 300px;
    height: auto;
    color: #fff;
    padding: 20px;
    border-right: 20px solid ;
    border-top: 20px solid #7e57c2;
    bottom: -20px;
`
