import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { IMG_url, Post_url } from '../../../constants/urls';
import { ImgPost, ImgProfile, Loading } from '../styledPost';
import Gif from '../../../constants/imgs/loading.gif'
import { Profile } from '../../../components/Profile';

export default function ViewPost() {
	const navigate = useNavigate();
	const [ infoPost, setInfoPost ] = useState([]);
	const [ count, setCount ] = useState(0)
	const [ infoProfile, setProfile] = useState(Profile)

	console.log(infoPost);

	const getPosts = () => {
		axios
			.get(Post_url, {
				headers: {
					Authorization: localStorage.getItem('tokenEddit')
				}
			})
			.then((res) => {
				setInfoPost(res.data);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	useEffect(() => {
			getPosts();
		},[getPosts()]
	);

	const idComments = (id) => {
		localStorage.setItem('idComments', id);
		navigate('/comments-post');
	};

	return (
		<div>
			<h1>Todos os Post</h1>
			<div>
				<ImgProfile src={infoProfile[0].profile}></ImgProfile>
				{infoPost[0].username}
				{infoPost[0].title}
				{infoPost[0].body}
				{infoPost[0].commentCount}
				{infoPost[0].voteSum}
			</div>
		</div>
	);
}
