import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Post_url } from "../../constants/urls";
import useForm from "../../hooks/useForm";

export default function Comments() {
  const navigate = useNavigate();
  const [ infoDetailPost, setInfoDetailPost] = useState([])
  const [form, onChange, clear ] = useForm({body:''})


  let idComments = localStorage.getItem('idComments')

  const getCommentsId = () => {
    axios.get(`${Post_url}/${idComments}/comments`, {
      headers: {
        Authorization: localStorage.getItem('tokenEddit')
      }
		})
    .then((res) => {
      setInfoDetailPost(res.data);
      console.log(res.data)
    })
  }

  const createComments = () => {
    axios.post(`${Post_url}/${idComments}/comments`, form, {
      headers: {
        Authorization: localStorage.getItem('tokenEddit')
      }
		})
    .then((res) => {
      alert('Comentário enviado com sucesso!')

    })
    .catch((err) => {
      console.log('error', err.response);
    })

  }

  const returnPosts = () => {
    localStorage.removeItem('idComments');
    navigate('/posts') 
  }
  const keyPressEnter = (e) => {
    if(e.key === 'Enter' && form.body !== ''){
      createComments();
    }
  }

  useEffect(() => {
    createComments()
    getCommentsId()
  }, [])

  return (
    <div>
      <h1>Escreva um comentário</h1>
      <form onSubmit={createComments}>
        <input onKeyPress={keyPressEnter} required name="body" type={'text'} value={form.body} onChange={onChange} placeholder="Envie seu comentário"></input>
        <button onKeyPress={keyPressEnter} onClick={createComments}>Enviar comentário</button>
      </form>
      <h1>Todos os comentários</h1>
      {infoDetailPost && infoDetailPost.length > 0 ? infoDetailPost.map((posts) => {
          return <div key={posts.id}>
            <div>
                <p>{posts.username}</p>
                <p>{posts.body}</p>
                <p>{posts.voteSum}</p>
            </div>
            </div>
        }) : <p>Carregando comentários</p>}
        <button onClick={returnPosts}>Voltar aos posts</button>
    </div>
  );
}
