import React from "react";
import useForm from "../hooks/useForm";
import axios from "axios";
import { useNavigate } from "react-router-dom"
import { Register_url } from "../constants/urls";

export default function FormRegister() {
  const navigate = useNavigate();

  const [ form, onChange ] = useForm({username: '', email:'', password: ''})


  const registerAccount = (event) => {
    event.preventDefault();

    axios.post(Register_url, form)
    .then((res) => {
      alert('Cadastro realizado com sucesso! Bom proveito')
      localStorage.setItem('tokenEddit');
      navigate('/login')
    })
    .catch((err) => {
      alert('Sentimos muito, mas não foi possível realizar seu cadastro')
    })

  }

  return (
    <div>
      <form onSubmit={registerAccount}>
        <input required name="email" type={'text'} value={form.username} onChange={onChange} placeholder="Crie seu nome de usuário"></input>
        <input required type={'email'} value={form.email} onChange={onChange} placeholder="Digite seu melhor email"></input>
        <input required minLength={'8'} maxLength={'30'} type={'password'} value={form.password} onChange={onChange} placeholder="Crie sua senha"></input>
      </form>
      <button onClick={registerAccount}>Fazer cadastro</button>
    </div>
  );
}
