import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import { Post_url } from "../../../constants/urls";
import useForm from "../../../hooks/useForm";

export default function ViewPost(){
  const navigate = useNavigate();
  const [ infoDetailPost, setInfoDetailPost] = useState([])
  const [ infoPost, setInfoPost] = useState([])
  const [form, onChange, clear ] = useForm({body: ""})

  const getPosts = () => {
    axios.get(Post_url, {
      headers: {
        Authorization: localStorage.getItem('tokenEddit')
      }
		})
    .then((res) => {
      setInfoPost(res.data);
    })
    .catch((err) => {
      console.log(err.response);
    })
  }

  useEffect(() => {
    getPosts();
  }, [])

  /* const idComments = (id) => {
    localStorage.setItem('idComments', id)
    navigate('/comments-post') 
  } */

  const idComments = (id) => {
      axios.get(`${Post_url}/${id}/comments`, {
        headers: {
          Authorization: localStorage.getItem('tokenEddit')
        }
      })
      .then((res) => {
        setInfoDetailPost(res.data);
      })
      .catch((err) => {
        alert('Problema na requisição dos comentários')
      })
  }

  return (
    <div>
      <div>
        {infoPost && infoPost.length > 0 ? infoPost.map((posts) => {
          return <div key={posts.id}>
            <div>
               <p>{posts.username}</p>
               <p>{posts.title}</p>
               <p>{posts.createdAt}</p>
            </div>
            <div>
              {posts.body}
            </div>
            <div>
              <p>{posts.voteSum}</p>
              <p onClick={() => idComments(posts.id)}>{posts.commentCount}</p>
            </div>
            </div>
        }) :  <p>Aguarde o carregamento dos posts...</p>}
      </div>

    </div>
  );
}
