import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { IMG_url, Post_url } from '../../../constants/urls';
import { ContainerCard, ContainerProfile, ImgPost, ImgProfile, Loading } from '../styledPost';
import Gif from '../../../constants/imgs/loading.gif'

export default function ViewPost() {
	const navigate = useNavigate();
	const [ infoPost, setInfoPost ] = useState([]);
	const [ dados, setDados] = useState([])

	const getPosts = () => {
		axios.get(Post_url, {
				headers: {
					Authorization: localStorage.getItem('tokenEddit')
				}
			})
			.then((res) => {
				setInfoPost(res.data);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	const getPhotos = () => {
		axios.get(IMG_url)
			.then((res) => {
				setDados(res.data.hits);
				console.log(res.data.hits);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	useEffect(() => {
			getPosts();
			getPhotos();
		},[getPosts()]
	);

	const idComments = (id) => {
		localStorage.setItem('idComments', id);
		navigate('/comments-post');
	};

	return (
		<div>
			<h1>Todos os Post</h1>
			<div>
				{infoPost && infoPost.length > 0 ? (
					infoPost.map((posts, index) => {
						return (
							<ContainerCard key={posts.id}>
								<ContainerProfile>
									<ImgProfile src={dados[index].userImageURL}></ImgProfile>
									<p>{posts.username}</p>
									<p>{posts.title}</p>
								</ContainerProfile>
								<div>
									<ImgPost src={dados[index].largeImageURL}></ImgPost>
									{posts.body}
									</div>
								<div>
									<p>{posts.voteSum}</p>
									<p onClick={() => idComments(posts.id)}>
										Ver todos os {posts.commentCount} comentários
									</p>
								</div>
							</ContainerCard>
						);
					})
				) : ( <div>
					<h1>Aguarde, pois estamos Carregando posts...</h1>
					<Loading src={Gif}></Loading>
					</div>
				)}
			</div>
		</div>
	);
}
