import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { IMG_url, Post_url } from '../../../constants/urls';
import {
	ContainerCard,
	ContainerInfo,
	ContainerProfile,
	ImgPost,
	ImgProfile,
	Lateral,
	Loading,
	ParagrafoText
} from '../styledPost';
import Gif from '../../../constants/imgs/loading.gif';

export default function ViewPost() {
	const navigate = useNavigate();
	const [ infoPost, setInfoPost ] = useState([]);
	const [ dados, setDados ] = useState([]);

	const getPosts = () => {
		axios
			.get(Post_url, {
				headers: {
					Authorization: localStorage.getItem('tokenEddit')
				}
			})
			.then((res) => {
				setInfoPost(res.data);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	const getPhotos = () => {
		axios
			.get(IMG_url)
			.then((res) => {
				setDados(res.data.hits);
			})
			.catch((err) => {
				console.log(err.response);
			});
	};

	useEffect(
		() => {
			getPosts();
			getPhotos();
		},
		[ getPosts() ]
	);

	const idComments = (id) => {
		localStorage.setItem('idComments', id);
		navigate('/comments-post');
	};

	return (
		<div>
			<h1>Todos os Post</h1>
			<div>
				{infoPost && infoPost.length > 0 ? (
					infoPost.map((posts, index) => {
						return (
							<ContainerCard key={posts.id}>
								<ContainerProfile>
									<Lateral>
										<ImgProfile src={dados[index].userImageURL} />
										<p>@{posts.username}</p>
									</Lateral>
									<ParagrafoText>{posts.title}</ParagrafoText>
								</ContainerProfile>
								<div>
									<ImgPost src={dados[index].largeImageURL} />
									{posts.body}
								</div>
								<ContainerInfo>
									<p>{posts.voteSum}</p>
									<p onClick={() => idComments(posts.id)}>
										Ver todos os {posts.commentCount} coment√°rios
									</p>
								</ContainerInfo>
							</ContainerCard>
						);
					})
				) : (
					<div>
						<h1>Aguarde, pois estamos Carregando posts...</h1>
						<Loading src={Gif} />
					</div>
				)}
			</div>
		</div>
	);
}
